'use strict';

var Resource = require('./Resource');

module.exports = Resources;

function Resources(options, tokenHandler) {
  this._options = options;
  this._tokenHandler = tokenHandler;
}

Resources.prototype.serviceOfferings = function () {
  return new Resource(this._options, this._tokenHandler, 'service_offerings');
};

Resources.prototype.servicePlans = function () {
  return new Resource(this._options, this._tokenHandler, 'service_plans');
};

Resources.prototype.instances = function () {
  return new Resource(this._options, this._tokenHandler, 'service_instances');
};

Resources.prototype.bindings = function () {
  return new Resource(this._options, this._tokenHandler, 'service_bindings');
};
