'use strict';
var util = require('util');
var XsODataError = require('./xsODataError');

module.exports = ApplicationError;

/**
 * Special error which the Application can use in an application exit to indicate an error
 * @param message
 * @param cause
 * @constructor
 */
function ApplicationError(message, cause) {
    XsODataError.call(this, message, cause);
}

util.inherits(ApplicationError, XsODataError);
