'use strict';
var util = require('util');
var HttpError = require('./httpError');

module.exports = DebugInfo;

/**
 * Error indicating an XSOData internal problem, e.g. used in try/catch statements to catch runtime errors
 * @param message
 * @param cause
 * @param context
 * @constructor
 */
function DebugInfo(message, context, cause) {
    HttpError.call(this, message, cause);
    this.context = context;
}

util.inherits(DebugInfo, HttpError);

