'use strict';
var util = require('util');
var HttpError = require('./../httpError');

module.exports = PreconditionFailed;

/**
 * Error indicating a "412 Precondition failed" http response code
 *
 * @param message
 * @param cause
 * @param context
 * @constructor
 */
function PreconditionFailed(message, context, cause) {
    HttpError.call(this, message, context,cause, 412);
    this.context = context;
}

util.inherits(PreconditionFailed, HttpError);
