'use strict';
var util = require('util');
var InternalError = require('./internalError');

module.exports = TypeError;

/**
 * Error during type convertion
 * @param message
* @param context
* @param information
* @constructor
*/
function TypeError(message, context, information) {
    InternalError.call(this, message, context);
    this.message = message;
    this.information = information;
}

util.inherits(TypeError, InternalError);
