'use strict';

function ConsoleTransport() {}

ConsoleTransport.prototype.log = function (message, callback) {
  console.log(message._content); // eslint-disable-line no-console
  if (callback) {
    callback(null);
  }
};

ConsoleTransport.prototype.updateConfigurationChange = function (id, state, callback) {
  if (callback) {
    callback(null);
  }
};

ConsoleTransport.prototype.updateDataModification = function (id, state, callback) {
  if (callback) {
    callback(null);
  }
};

module.exports = ConsoleTransport;
