'use strict';

module.exports = {
  error: error,
  ok: ok,
  continue: continuePolling
};

function error(message, statusCode) {
  var err = new Error(message);
  if (statusCode) {
    err.statusCode = statusCode;
  }
  return { err: err };
}

function ok(data) {
  return { ok: true, data: data };
}

function continuePolling() {
  return {};
}
