'use strict';

var isPlainObject = require('../utils').isPlainObject;

module.exports = {
  processSmErrorsPropertyValue
};

function processSmErrorsPropertyValue(errors) {
  if (isPlainObject(errors)) {
    errors = [errors];
  }
  if (Array.isArray(errors)) {
    return errors.map(function (error) { return processSmErrorObject(error); }).join('; ');
  }
  return errors;
}

function processSmErrorObject(errObj) {
  var str = 'error: ' + errObj.error;
  if (errObj.description) {
    str += ', description: ' + errObj.description;
  }
  return str;
}
