"use strict";

var http = require('http');
var fs = require('fs');
var TextBundle = require('..');
var pathToFile = "../test/properties/i18n";
var txtBundle;


// Configure our HTTP server to respond with Hello World to all requests.
var server = http.createServer(function (request, response) {
    response.writeHead(200, {
        "Content-Type": "text/plain"
    });
    txtBundle = new TextBundle({
        path: pathToFile,
        locale: "de"
    });
    response.end(txtBundle.getText('greeting', ["Stefan", "21"]));
    //response.end(JSON.stringify(txtBundle));
    //  var text = txtBundle.getText('hello');
    //  response.end(JSON.stringify(text.originInfo));
});

// Listen on port 8099, IP defaults to 127.0.0.1
server.listen(8099);

// Put a friendly message on the terminal
console.log("Server running at http://127.0.0.1:8099/");
