'use strict';
var util = require('util');
var HttpError = require('./../httpError');

module.exports = NotModified;

/**
 * Error indicating a "304 Not modified" http response code
 *
 * @param message
 * @param cause
 * @param context
 * @constructor
 */
function NotModified(message, context, cause) {
    HttpError.call(this, message, context,cause, 304);
    this.context = context;
}

util.inherits(NotModified, HttpError);
