'use strict';
var util = require('util');
var HttpError = require('./../httpError');

module.exports = PreconditionRequired;

/**
 * Error indicating a "428 Precondition required" http response code
 *
 * @param message
 * @param cause
 * @param context
 * @constructor
 */
function PreconditionRequired(message, context, cause) {
    HttpError.call(this, message, context,cause, 428);
    this.context = context;
}

util.inherits(PreconditionRequired, HttpError);
