'use strict';
var util = require('util');
var HttpError = require('./../httpError');

module.exports = Unauthorized;

/**
 * Error indicating an authorization problem
 * @param message
 * @param cause
 * @param context
 * @constructor
 */
function Unauthorized(message, context, cause) {
    HttpError.call(this, message, context,cause, 401);
    this.context = context;
}

util.inherits(Unauthorized, HttpError);

